float wave_amp=0.02;
float wave_freq=3.0;
float wave_speed=5.0;


bool red_strip( vec2 uv )
{
    if (uv.y>-0.06 && uv.y<0.06)
        return true;

    if (uv.y>-0.3 && uv.y<-0.18)
        return true;

    if (uv.y>0.18 && uv.y<0.3)
        return true;

    return false;
}

vec4 yellow_flag(vec2 uv)
{
    if (red_strip(uv))
        return vec4(0.98, 0.24, 0.2, 1.0);

    return vec4(1.0, 1.0, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 v = fragCoord.xy / iResolution.xy;
    vec2 uv=v*2.-vec2(1.);
    uv.x*=iResolution.x/iResolution.y;

    float d=-uv.x+0.3*uv.y + texture(iChannel0,uv).x*0.03 - exp(-1.0+uv.x) +  exp(-uv.y);
    float a=mod(wave_speed*iTime+d*wave_freq,4.0*3.14159265359);
    uv.x += cos(a)*wave_amp;
    uv.y += sin(a)*wave_amp;
    vec4 color = yellow_flag(uv);
    float shadow = .75 - 0.25*(sin(a-0.3));
    fragColor = color * shadow;
}
