// Fork of "CircleWave" by RenoM. https://shadertoy.com/view/4dc3D8
// 2019-08-27 00:50:56

#define SCALE 40.
#define SPEED 4.
#define FREQUENCY -.2

float d;
// Circle
// was: #define C(p)  min(1., sqrt(10.*abs(length(p-.5)-.4)))
#define C(p, o)  min(1., sqrt(10.*abs(length((p-.5)/(o + .001))-.4)))
// Little dot
#define D(p,o)  ( (d=length(p-o)*5.)<=.6 ? d:1. )

void mainImage( out vec4 O, in vec2 U )
{
    vec2 R = iResolution.xy;
    U = U - iMouse.xy + R*.5;
    vec2 p = SCALE*(U+U/R)/R.y,
         f = fract(p);
    vec2 q = floor(p) + vec2(1., -1.) - SCALE*vec2(.9, .45);
    float a = atan(q.y, q.x);
    float t=(length(q) - a * .25 / FREQUENCY)*FREQUENCY
           +iTime*SPEED;
    vec2 o=vec2(cos(t),sin(t)); // was *.4+.5;
    float col = C(f, o); // was: *D(f,o);
    O.xyz = vec3(1.0 - col, 1.0 - col * .7, 0.5);
}
