#define hc 0.499
#define hf 0.0001
#define srk 0.03;

vec3 zoomzoom(vec2 uv, float os, in vec3 incol) {
    vec3 col = vec3(os);
    float h = min(abs(sin(iTime / 2. + os)), hc);
    float o = h + srk;

    float hpct = 0.;
    float opct = 0.;

    vec2 bl = step(vec2(h), uv);
    vec2 tr = step(vec2(h), 1.0-uv);
    hpct = bl.x * bl.y * tr.x * tr.y;

    vec2 obl = step(vec2(o), uv);
    vec2 otr = step(vec2(o), 1.0-uv);
    opct = obl.x * obl.y * otr.x * otr.y;

    col = mix(incol, vec3(1.), hpct);
    col = mix(col, vec3(0.), opct);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.);

    col = zoomzoom(uv, 0., col);
    col += zoomzoom(uv, 0.2, col);
    col += zoomzoom(uv, 0.4, col);
    col += zoomzoom(uv, 0.6, col);
    col += zoomzoom(uv, 0.8, col);
    col += zoomzoom(uv, 1., col);

    fragColor = vec4(col,1.0);
}
