float ln (vec3 p, vec3 a, vec3 b) {return length(p-a-(b-a)*min(dot(p-a,b-a),0.)/dot(b-a,b-a));}
void mainImage( out vec4 Q, in vec2 U )
{
 	vec4 
        n = D(U+vec2(0,1)),
        e = D(U+vec2(1,0)),
        s = D(U-vec2(0,1)),
        w = D(U-vec2(1,0));
    vec4 a = A(U);
    Q = C(U);
    Q = (Q.xxxx)*vec4(.7,.8,.9,1);
    vec3 no = normalize(vec3(e.x-w.x,n.x-s.x,1));
    vec3 re = reflect(normalize(vec3((U-0.5*R)/R.y,1)),no);
    float light = ln(vec3(2,2,2),vec3(U/R.y,0),vec3(U/R.y,0)+re);
    Q *= (5.*exp(-2.*light*vec4(1,2,3,4))+.8*exp(-.3*light))*(0.5+0.5*dot(re,normalize(vec3(U/R.y,0)-vec3(2,2,2))));
	Q = atan(3.*Q)*.666;

    U = 0.5*R-abs(U-0.5*R);
    Q += (0.5-0.5*cos(6.*a.z*(1.+.5*vec4(1,2,3,4))))*smoothstep(1.,-1.,min(U.x,U.y)-6.);


}

// Calculate forces and pressure
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    Q.xy = mix(Q.xy,0.25*(n.xy+e.xy+s.xy+w.xy), .5);
    Q.xy -= 
        // pressure force
        0.25*vec2(e.z-w.z,n.z-s.z)+
        // magnus force
        0.25*Q.w*vec2(n.w-s.w,e.w-w.w);
    // divergence
    Q.z  = 0.25*(s.y-n.y+w.x-e.x+n.z+e.z+s.z+w.z);
    // curl
    Q.w = 0.25*(s.x-n.x+w.y-e.y);
    vec4 d = D(U);
    Q.xy -= d.zw;
    Q.y -= .001*d.x;
    //Boundary conditions
    vec2 JET = vec2(.5+.25*sin(float(iFrame)/500.),0.6)*R;
    if (iMouse.z>0.) JET = iMouse.xy;
    Q.xy = mix(Q.xy,.5*vec2(cos(iTime),sin(iTime)),0.3*smoothstep(1.,-1.,length(U-JET)-11.));
    if (iFrame<1) Q = vec4(0);
    if (U.x<4.||U.y<4.||R.x-U.x<4.||R.y-U.y<4.)Q.xyw*=0.;
}

// Advect along velocity and curl feild
void mainImage( out vec4 Q, in vec2 U )
{
    for (float i = 0.; i< N;i++) {
        Q = A(U);
        float co = cos(Q.w/N), si = sin(Q.w/N);
        U -= Q.xy*mat2(co,-si,si,co)/N;
    }
    Q = A(U);
}

// Advect along velocity and curl feild
void mainImage( out vec4 Q, in vec2 U )
{
    for (float i = 0.; i< N;i++) {
        Q = A(U);
        float co = cos(Q.w/N), si = sin(Q.w/N);
        U -= Q.xy*mat2(co,-si,si,co)/N;
    }
    Q = C(U);
}

// draw color and acount for divergence
void mainImage( out vec4 Q, in vec2 U )
{
    Q = C(U);
    // neighborhood
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0)),
    	nc = C(U+vec2(0,1)),
        ec = C(U+vec2(1,0)),
        sc = C(U-vec2(0,1)),
        wc = C(U-vec2(1,0));
    // divergence 
    vec4 m = 0.25*(nc+ec+sc+wc);

    #define a .01

    Q.y = mix(mix(Q.y,m.y,1.-a),Q.x,a);
    Q.zw = .25*(Q.y-.5)*vec2(ec.y-wc.y, nc.y-sc.y);

    Q += 0.25*(s.y*sc-n.y*nc+w.x*wc-e.x*ec);

    // boundary conditions
    vec2 JET = vec2(.5+.25*sin(float(iFrame)/500.),0.6)*R;
    if (iMouse.z>0.) JET = iMouse.xy;
    Q = mix(Q,vec4(1,0,0,0),smoothstep(1.,-1.,length(U-JET)-10.));
    if (iFrame<1)Q = vec4(0);
    if (U.x<4.||U.y<4.||R.x-U.x<4.||R.y-U.y<4.)Q*=0.;
}
