float uv2TVHoleShape(vec2 uv,vec2 scale)
{
    uv = uv * vec2(2) - vec2(1);
    uv = abs(uv);

    uv *= scale;
    float y = smoothstep(1.0,0.0,uv.x);

    return float(y > uv.y);
}
float uv2TopBottomBlack(vec2 uv, float amount01)
{
    uv.y = uv.y * 2. - 1.;
    uv.y = abs(uv.y);
    return float(uv.y < (1.-amount01));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;   

    vec3 col = vec3(texture(iChannel0, uv)) * vec3(0,1,0);//original color
    float param01 = sin(iTime * 3.) * 0.5 + 0.5; //on off param from 0 to 1

    //---------------------------------------------------------------------------       
    float cutPoint = 0.5;//can edit this num

    float anim01;

    float topBottomBlackColor;
    anim01 = param01 / (cutPoint);
    anim01 = anim01 * anim01 * anim01;
    anim01 /= 1.05;
    topBottomBlackColor = uv2TopBottomBlack(uv, anim01);

    float TVHoleShapeColor;
    anim01 = (param01 - cutPoint) / (1.-cutPoint);
    float x = (anim01*anim01*anim01) * 80. + 1.;
    float y = (anim01*anim01) * 80. + 2.; //y start @ half scale
    TVHoleShapeColor = uv2TVHoleShape(uv, vec2(x,y));

    float blendRange = 0.2;
    col *= mix(topBottomBlackColor,TVHoleShapeColor,smoothstep(cutPoint-blendRange,cutPoint+blendRange, param01));
  	//---------------------------------------------------------------------------

    // Output to screen
    fragColor = vec4(col,1.0);
}
