void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/iResolution.xy;
    vec4 vanDam = texture(iChannel0, xy);
    vec4 hz = texture(iChannel1, xy);
    vec4 London = texture(iChannel2, xy);
    if (vanDam.g-.2 > max(vanDam.r, vanDam.b)) {
    	if (hz.g-.1 > max(hz.r, hz.b)) {
            fragColor = London;
        } else {
            fragColor = hz;
        }
    } else {
    	fragColor = vanDam;
    }
}
