vec3 polarShape(vec2 uv)
{
    vec3 color = vec3(0.0);
	uv=fract(uv*10.0);
    vec2 pos = vec2(0.5)-uv;

    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);

    float f = cos(a*5.);
     f = abs(cos(a*3.));
     f = abs(cos(a*2.5))*.5+.3;
    // f = abs(cos(a*12.)*sin(a*3.))*.8+.1;
    // f = smoothstep(-.5,1., cos(a*10.))*0.2+0.5;

    color = vec3( 1.-smoothstep(f,f+0.02,r) );
	return color;
}

vec3 grid(vec2 uv)
{
    uv=fract(uv*10.0);
    // bottom-left
    vec2 bl = step(vec2(0.02),uv);
    float pct = bl.x * bl.y;

    // top-right
     vec2 tr = step(vec2(0.02),1.0-uv);
     pct *= tr.x * tr.y;

    return vec3(pct);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy/min(iResolution.x,iResolution.y);

    if(uv.x>iMouse.x/min(iResolution.x,iResolution.y))
        fragColor=vec4(polarShape(uv),1.0);
    else
        fragColor=vec4(grid(uv),1.0);
}
