renderpass.code:
            //           normalized so that radius = 1
#define illu(x) ( d = clamp( 2.*(x-.7) ,-1.,1.),                   \
                  1. - ( acos( d ) -  d*sqrt(1.-d*d) ) / 3.1416    )
          // compl( ( caps =  sector  -  triangle  )   % plain disc area )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 10.*u/R.y,
         F = 2.*fract(U)-1.,
         I = floor(U),
         A = abs(F+vec2(.1*I.x-.1,0));
    O-=O;
    float d;
    int y = int(U.y);
    O +=  y==8 ? .2+.8*smoothstep(30./R.y,0.,length(F)-.5) 
                   - step(max(A.x,A.y),.7)
        : y==6 ? illu(.1*I.x-.1)
        : y==4 ? illu(.1*U.x-.1)
        : y<=3 ? 1. - R.y/5.* abs( U.y/3.-.2 - illu(.1*U.x-.1) )
        : 0.;

    if (y<=3) O.r = max( O.r, 1. - R.y/5.* abs( U.y/3.-.2 - smoothstep(0.,1.,.5+.5*d) )), // for comparison
              O.b = max( O.b, 1. - R.y/5.* abs( U.y/3.-.2 -  (.5+.5*d) ));
    O = pow(O, vec4(1./2.2));  // to sRGB
}
