#define R2 1.41421356237
#define r (iResolution.xy)
#define t (iTime*(1.-iMouse.x/r.x))
#define M(q)q*=mat2(sin(t*.1-t/s+vec4(0,11,33,0)))

void mainImage( out vec4 o, in vec2 FC )
{
    o=vec4(0);
	float s,g,e,i;
    for(i=g=0.;i++<20.;){
        e=s=2.;
        vec4 p=vec4(g*(FC.xy*e-r)/r.y,g-1.7,1);
        for(int i;i++<24;s*=R2)
            M(p.xy),
            M(p.yz),
            p=abs(p)*R2-R2+1.;
        g+=e=length(p)/s;
        o+=(1e-5-p/s*.05)/e;
    }
}
