#define PI 3.14159265359

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

	float tiling = 5.0;
    st *= tiling;
    //st = fract(st);

    float noiseTiling = 0.01;
    float noiseIntensity = 0.7;
    vec2 distortion = texture(iChannel1, st * noiseTiling).xy * noiseIntensity;

    // repeat uv on a grid with distorted bounds
    vec2 distortedSt = st + distortion;
    vec2 coord = floor(distortedSt);
    st -= coord;

    // rotate around center
    st -= 0.5;
    float randomId = random(coord);
    st = rotate2d( randomId*PI*2.0 ) * st;
	st += 0.5;

    // Output to screen
    float textureTiling = 0.5;
    fragColor = texture(iChannel0, st * textureTiling);
}
