//A lego variation of https://www.shadertoy.com/view/wljyWc

#define CHANNEL iChannel0
#define DENSITY 35.0
#define SQUARES false

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = vec4(0.0,0.0,0.0,0.0);

    vec2 uv = fragCoord/iResolution.xy;
    //Aspect fix
    uv.y *= iResolution.y / iResolution.x;

    //Grid position
    vec2 gid = floor(uv * DENSITY);
    //Grid cell uv (from -1 to 1) 
    vec2 guv = (fract(uv * DENSITY)-0.5)*2.0f;

    vec4 cellColor = texture(CHANNEL, gid/DENSITY * vec2(1.0, iResolution.x / iResolution.y)); //Revert the aspect back to correct.
    float brightness = length(cellColor) / 1.7320508075688772;



    if(SQUARES)
    {
        float circleSize = 1.0;

        //Manhattan distance
        float distance = max(abs(guv.x), abs(guv.y));
		color = cellColor * (
            smoothstep(-0.1,0.0, circleSize - distance) + //Outer edge, -0.1 so the edge doesnt look so crisp
            smoothstep(0.0, 0.2, circleSize - distance)*0.1); //Darker area near edges
    }
    else
    {
        float circleSize = 0.9;

        float distance = length(guv);
		color = cellColor * (
            smoothstep(-0.1,0.0, circleSize - distance) - //Outer edge, -0.1 so the edge doesnt look so crisp
            smoothstep(0.2, 0.3, circleSize - distance)*0.35 + //Darker area near knob
            smoothstep(0.3, 0.35, circleSize - distance)*0.5); //Knob



    }


    fragColor = color;
}
