// variant of https://shadertoy.com/view/3lScRR
// variant of https://shadertoy.com/view/ttlcDs

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy*.1, V = u/R.y, C,
    U = V*5. + V.y* cos( iTime + V.x*.56 + 11.*O.xw );
    float d, s=0.;
        for (int k=0; k<25; k++)
            C = U - round((U+vec2(k%5-2,k/5-2))*.2) / .2,
            V = C * inverse(transpose(mat2(dFdx(U),dFdy(U)) ))/R.y,
            d = max(0.,1.-.05*dot(C,C)), d*=d*d*d,
            O += texture(iChannel0,.5+V) * d , s += d;
    O *= smoothstep( 1.,-1., U.y -37.) / s;
}
