// v2
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv*=1.0+0.07*sin(iTime*0.3);
    float y=0.5;
	float y0=-2.0;//2.4;
	float w=1.0;//1.6;
    float r=y0+w*(uv.x);
    float col=0.0;
    #define N 305
    float dc = 40.0/float(N);
    for(int i=0; i<N; i++)
    {
    	y = r*y*(1.0-y);        
       	if( abs(y - uv.y) < 0.002)
    		col += dc;
    }
    fragColor = vec4(vec3(col),1.0);
}

