float colorBand1(float value, float colorSteps) {
	return floor(value * colorSteps) / colorSteps;
}

float colorBand2(float value, float colorSteps) {
    float currentStep;
    float stepSize = 1.0 / colorSteps;
    modf(value / stepSize, currentStep);
	return currentStep * stepSize;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	float o = colorBand1(uv.y, 8.0);

    // Output to screen
    fragColor = vec4(o, o, 1.0,1.0);
}
