float HexDist(vec2 p){
	p = abs(p);

    float c = dot(p,normalize(vec2(1,2.236)));
    return max(c,p.x);
}

vec4 HexCoords(vec2 uv){
	vec2 r = vec2(1.,1.73);
    vec2 h = r*.5;
    vec2 a = mod(uv,r)-h;
    vec2 b = mod(uv-h,r)-h;

    vec2 gv;
    if(length(a)<length(b))
    	gv = a;
    else
       gv = b;

    float x = atan(gv.x,gv.y);
    float y = .5-HexDist(gv);
    vec2 id = uv-gv;
    return vec4(x,y,id.x,id.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord)/iResolution.y;
    vec2 ss = uv;
    vec2 m = (iMouse.xy)/iResolution.y;
	uv -= m;
    vec4 col = vec4(0);

    uv *= 16.;

    vec4 hc = HexCoords(uv);
    float d = (distance(ss,hc.zw)+.5);
    float scrollSpeed = 1.5;
    float c = sin(d*2. - iTime*scrollSpeed);

    col += c;

    fragColor = vec4(col);
}
