#define rotate(a)                     mat2(cos(a), -sin(a), sin(a), cos(a) )
#define draw_circle( pos, r, uv)   r / length(uv - pos)

float draw_scene(vec2 uv) {
    float mask = 0.0;

    for (float y = -1.; y < 1.; y += .3) {
        for (float x = -1.0; x < 1.5; x += .3) {
            vec2 cp = vec2(x, y);
            float intensity = log(.08/length(uv - cp));
            float size = cp.y/cp.x * sin(iTime) * 0.2;
            float brightness = .1;
            mask += draw_circle(cp, size, uv) * brightness * intensity;
        }
    }

    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord -.5*iResolution.xy)/iResolution.y;
    mat2 rot_mat = mat2(cos( iTime + vec4(0, 33, 11, 0)));
    uv *= rot_mat;

    float mask = draw_scene(uv);
    vec3 col = vec3(1.) * mask;

	fragColor = vec4(col, 1.0);
}
