void mainImage(out vec4 color, vec2 coord) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = coord * ps;

    vec2 mouse = iMouse.xy * ps;
    mouse.x = (iMouse.z > 0.0)
        ? mouse.x
        : abs(fract(iTime * 0.075) - 0.5) * 2.0;
        //: sin(iTime * 0.3) * 0.5 + 0.5;

    if (mouse.x < 0.5)
    	uv.x = (uv.x < mouse.x)
        	? mouse.x - (uv.x - mouse.x)
        	: uv.x;
    else
        uv.x = (uv.x > mouse.x)
        	? mouse.x - uv.x + mouse.x
        	: uv.x;

    color = texture(iChannel0, uv);
}
