#endif

void mainImage(out vec4 c, vec2 p)
{
    vec2 R = iResolution.xy
    , uv = p/R
    , q = (p+p-R)/R.y
    , s = vec2(0)
    ;
    c.rgb = .5 + .5 * cos(.1*iTime + uv.xyx + vec3(0,2,4));

    float l = distance(q, s) - .5 // circle    
    , a = smoothstep(.4, -.4, l)
    , u = p.x
  //  * p.y // looks better on my monitor without y; can do either, or both.
    , check = step(fract(.5*u), .5);

    check = mix(.5, check, a);
    c.rgb *= check;
    // obviously gamma is wrong without OETF.  This is why we test!
    //c.rgb = sqrt(c.rgb); // usually close enough
    c.rgb = pow(c.rgb, vec3(1./2.2)); // to sRGB gamut, approximate
    //c.rgb = linear2srgb(c.rgb); // to sRGB gamut, more accurate
    c.a = 1.;
}

