#define F(p) texelFetch(iChannel0,p,0)
#define B(p) texelFetch(iChannel1,p,0)
#define t iTime

void mainImage(out vec4 S, in vec2 U) {
    vec2  R = iResolution.xy,
          Q = iChannelResolution[0].xy,
          c = .25*vec2(4.+cos(t)-(t<400.?0.:(t-400.)*.1),.2*sin(t)),
          z = (2.*U-R)/R; z*=vec2(z.x,t<204.?z.y:-z.y);
    float H = log(length(z)),
          E = atan(z.y,z.x),
          C = exp(-E*c.y+c.x*H),
          o = c.y*H+c.x*E;
    ivec2 m = ivec2(mod(.5*(vec2(C*cos(o),C*sin(o))+1.)*Q,Q));
    vec3  e = F(m).xyz;
    	  S = (atan(dot(e,vec3(.2,-.5,.3)),dot(e,vec3(.6,-.3,-.3)))<-.4?B(m):F(m));
}
