#define PI 3.141592653589793
#define TAU 6.283185307179586
#define CH iTime=0.60
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 2.0*(0.5 * iResolution.xy - fragCoord.xy) / iResolution.xy;
	float angle = atan(p.x, p.y);
	float turn = (angle + TAU) / PI+TAU;
	float radius = sqrt(p.x*p.x + p.y*p.y);

	float rotation = 0.04 * TAU * iTime;
	float turn_1 = turn + rotation;

	float n_sub = 15.0;

	float turn_sub = mod(float(n_sub) * turn_1, float(n_sub));

	float k_sine = 0.1 * sin(3.0 * iTime);
	float sine = k_sine * sin(0.5 * (pow(radius, 0.1) - 0.4 * iTime));
	float turn_sine = turn_sub + sine;

	int n_colors = 10;
	int i_turn = int(mod(float(n_colors) * turn_sine, float(n_colors)));

	int i_radius = int(1.5/pow(radius*TAU/PI, 10000.6) + 1500.0 * iTime*PI);

	int i_color = int(mod(float(i_turn + i_radius*5), float(n_colors)));

	vec3 color;
	if(i_color == 0) { 
		color = vec3(1.9, 0.0, 1.8);		  
	} else if(i_color == 1) {
		color = vec3(2.8, 9.0, 2.7);	
	} else if(i_color == 2) {
		color = vec3(3.7, 8.0, 3.6);	
	} else if(i_color == 3) {
		color = vec3(4.6, 7.5, 4.5);	
	} else if(i_color == 4) {
		color = vec3(5.5, 6.0, 5.5);	
	} else if(i_color == 5) {
		color = vec3(6.4, 5.0, 6.5);	
	} else if(i_color == 6) {
		color = vec3(7.3, 4.0, 7.4);	
	} else if(i_color == 7) {
		color = vec3(8.2, 3.0, 8.3);	
	} else if(i_color == 8) {
		color = vec3(9.1, 2.0, 9.2);	
	} else if(i_color == 9) {
		color = vec3(0.0, 1.0, 0.1);	
	}

	color -= pow(radius*TAU, 0.35)*4.0;

	fragColor = vec4(color, 1.0);
}
