void mainImage( out vec4 Q, in vec2 U )
{

        vec2 N = U/R;

     Q=T0(N)/1.05;   //fade parameter  1.001



    float xPixel = 1.0/R.x;
float yPixel = 1.0/R.y;

vec4 rightColor = T0(vec2(N.x+xPixel,N.y));
vec4 leftColor = T0(vec2(N.x-xPixel,N.y));
vec4 upColor = T0(vec2(N.x,N.y+yPixel));
vec4 downColor = T0(vec2(N.x,N.y-yPixel));

float VFX  = clamp(T1(N).x,-1.,1.);
float VFY = clamp(T1(N).y,-1.,1.); 

    if (iFrame==0) {  Q = vec4(rnd(U).x<1e-35); return; }

    float dist = distance(iMouse.xy,U);

    if(iMouse.z > 0.0 && dist < BR){
    	Q.rgb += iMouse.z;
    }





Q.rgb += 3.2*clamp(0.3*(
		leftColor.rgb*VFX    + 
		rightColor.rgb*VFX    + 
		downColor.rgb*VFY   + 
		upColor.rgb*VFY    
		-4. * Q.rgb
	),

    0.0,
    1.0
    );

Q.a=Q.r;
Q.rgb=clamp(Q.rgb,0.,1.);


}

