#define NOISE_LEVEL 0.25
#define DARKEN_FACTOR 0.125

#define DENOMINATOR vec2(213, 5.53)
#define NEG_X vec2(-1.0, 1.0)
#define NEG_Y vec2(1.0, -1.0)

#define GLITCH_OFFSET_1 vec2(32.05, 236.0)
#define GLITCH_OFFSET_2 vec2(-62.05, -36.0)

float rand(vec2 co)
{
	return fract(sin(dot(co.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Scanline desync
	if (mod(iTime, 2.0) > 1.9)
		uv.x += cos(iTime * 10.0 + uv.y * 1000.0) * 0.01;

    // Pixelate glitch 1
    if (mod(iTime, 4.0) > 3.0)
		uv = floor(uv * 32.0) / 32.0;

    // Pixelate glitch 2
	if (mod(iTime, 5.0) > 3.75)
    	uv += 1.0 / 64.0 * (2.0 * vec2(
            rand(floor(uv * 32.0) + GLITCH_OFFSET_1),
            rand(floor(uv.y * 32.0) + GLITCH_OFFSET_2)) - 1.0);

	fragColor = texture(iChannel0, uv);
    fragColor.rgb
        += NOISE_LEVEL
        * vec3(rand(iTime + fragCoord / DENOMINATOR * NEG_X),
               rand(iTime - fragCoord / DENOMINATOR * NEG_Y),
               rand(iTime + fragCoord / DENOMINATOR))
        - DARKEN_FACTOR;
}
