#define PI 3.14159265359
#define MULTIPLIER 100.
#define MODULE 200.

vec4 get_color(vec2 uv, float mul, vec4 color) {
    float step_angle = 0.2 * PI / float(MODULE);
    float start_angle = PI / 2.0;
    float end_angle = PI / 2.0;

    for (float i = 0.; i < MODULE; i++) {
        vec2 line_start = vec2(sin(start_angle), cos(start_angle));
        if (distance(line_start, uv) < .01)//Point thickness.
            return vec4(0., 0.0, .0, 1.); //Point color.

        vec2 line_end = vec2(sin(end_angle), cos(end_angle));
        vec2 line_dir = line_end - line_start;
        float distance_to_line = abs(line_dir.y * uv.x -line_dir.x * uv.y + line_start.x * line_end.y - line_start.y * line_end.x) / length(line_dir);
        if (distance_to_line < 2. / iResolution.y &&
                dot(uv - line_start, line_dir) > 0. &&
           		dot(uv - line_end, line_dir) < 0.)
            return color;

        start_angle += step_angle;
        end_angle += mul * step_angle;
    }

    return vec4(0.);//Background color.
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord + fragCoord - iResolution.xy) / (iResolution.y+(iTime*2.5));//Ball size.

    float mul = 1.0 + MULTIPLIER * (1. - cos(.03 * iTime));//Speed.
    vec4 color = vec4(sin(1.5 * iTime),//Random Color.
                      cos(.2 * iTime),
                      cos(.3 * iTime),
                      1.);

    fragColor = get_color(uv, mul, color);
}
