#define MAX_ITER 4

vec2 iterate_one(in vec2 z, in vec2 c)
{
    return vec2(z[0]*z[0]-z[1]*z[1]+c[0], 2.0*z[0]*z[1]+c[1]);
}

vec3 inside(in vec2 c, float freq, float green, float red)
{
    vec2 start = vec2(0.0, 0.0);
    for(int i = 0; i < MAX_ITER; i++)
    {
        start = vec2(start[0]*start[0]-start[1]*start[1]+c[0]+green, 2.0*start[0]*start[1]+c[1]+green);
    }

    vec2 new = iterate_one(start, c);
    vec2 diff = new-start;
    float speed = log(sqrt(diff[0]*diff[0]+diff[1]*diff[1]));
    float inside=0.0;
    float dir = atan(new[1]/new[0]);
    float val =  sqrt(start[0]*start[0]+start[1]*start[1]);
    return vec3(sin(freq*speed)*green*2.0-speed,red-1.0/sin(speed*exp(1.0/freq)*sin(green-red)), red-cos(dir/freq*cos(1.0/cos(speed+red))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec4 sample1 = texture(iChannel0, uv);
     vec4 brit = texture(iChannel1,uv);
    vec2 offset = vec2(-0.5,-0.5);
    uv = (uv+offset)*1.0;


    vec3 vals = inside(uv,sample1[0], brit[1], brit[0]);
    vec4 color = vec4(vals[0],vals[1],vals[2],1);
    fragColor = color; return;
}
