float smin(float a, float b, float k) {
 	float h = clamp(.5 + .5 * (b - a) / k, 0.0, 1.0);   
    return mix(b, a, h) - k * h * (1.0 - h);
}

float sphere(vec3 pos, float radius){
 	return length(pos) - radius;   
}

float scene(vec3 pos){
 	float s1 = sphere(pos - vec3(-.3, 0.1, 0.0), .3);
    float s2 = sphere(pos - vec3(.3, 0., 0.), .3);

    return smin(s1, s2, .15);
}

vec3 estimateNormal(vec3 pos){

 	return normalize(
    vec3(
        scene(pos - vec3(.001, .0, .0)) - scene(pos + vec3(.001, .0, .0)),
        scene(pos - vec3(.0, .001, .0)) - scene(pos + vec3(.0, .001, .0)),
        scene(pos - vec3(.0, .0, .001)) - scene(pos + vec3(.0, .0, .001))

    ));   
}


vec3 light_dir = vec3(.0, -1.0, 1.0);

vec3 trace(vec3 camOrigin, vec3 dir) {
 	vec3 ray = camOrigin;
    float totalDist = 0.0;
    for(int i = 0; i < 128; i++) {
     	float dist = scene(ray);
        if (abs(dist) < .001) {
            float diffuse = dot(light_dir, estimateNormal(ray));
            vec3 ambient = vec3(.2, .2, .2);
            return vec3(diffuse * .5) + ambient;

        }
        totalDist += dist;
        ray += dist * dir;
    }

    return vec3(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv = uv * 2.0 - 1.0;
    uv.y /= iResolution.x / iResolution.y;

    vec3 camOrigin = vec3(0.,0.,-1.);
    vec3 rayOrigin = vec3(camOrigin.xy + uv, camOrigin.z + 1.0);
    vec3 dir = normalize(rayOrigin - camOrigin);
    vec3 color = trace(camOrigin, dir);

    // Output to screen
    fragColor = vec4(color,1.0);
}
