//#define DEBUG

vec2 hash2( vec2 p ) {    
    return normalize(textureLod( iChannel0, (p + .5)/256., 0.).xy * 2. - 1.);
}

float perlin(vec2 uv) {
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    //cubic hermite curve: f(x) = x*x*(3.0-2.0*x)
    //vec2 t = smoothstep(vec2(0.), vec2(1.), f);

    //quintic interpolation curve:
    vec2 t = f*f*f*(f*(f*6.-15.)+10.);

    return mix( mix(dot(hash2(i                ), f                ), dot(hash2(i + vec2(1., 0.) ), f - vec2(1.,0.) ), t.x),
                mix(dot(hash2(i + vec2(0., 1.) ), f - vec2(0., 1.) ), dot(hash2(i + vec2(1., 1.) ), f - vec2(1.,1.) ), t.x),
                t.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord / iResolution.xx;

    //displacement
    uv -= iMouse.xy / iResolution.xy;

    //scale
    uv *= 10.;

    vec3 col = vec3(perlin(uv) * .5 + .5);

#ifdef DEBUG
    vec2 f = fract(uv);

    float size = .015;
    vec3 col1 = vec3(0., 1., 0.);

    col = mix(col1, col, min( smoothstep(.0, size, f.x), smoothstep(1., 1. - size, f.x) ) );
    col = mix(col1, col, min( smoothstep(.0, size, f.y), smoothstep(1., 1. - size, f.y) ) );
#endif

    // Output to screen
    fragColor = vec4(col,1.0);
}
