// 277 chars
void mainImage(out vec4 F, vec2 C)
{
    vec2 R = iResolution.xy;
    vec2 U = abs((2.*C-R)/R.y)-4.;
    float a = mod(atan(U.y,U.x),1.26);
    U = vec2(cos(a),sin(a))*length(U);
    U.x *= sign(cos(floor(U.x*12.)));
    U = fract(U*6.)-.5;
    F = vec4(smoothstep(.02,.04,abs(
                                    abs(
                                        abs(length(U-sign(U.x+U.y)*.5)
                                        -.5)
                                    -.1)
                                -.05)
                         )
             );
}
