bool isBlack(in vec3 c) {
    return c.r==0. && c.g==0. && c.b==0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv   = fragCoord/iResolution.xy;
    vec3 mask = texture(iChannel1, uv).rgb;
    vec3 col  = isBlack(mask) ? vec3(1) : texture(iChannel0, uv).rgb;   

    fragColor = vec4(col, 1.0);
}

float pattern(in vec2 pos, in float varX, in float varY) {
    return mod(pos.x*varX + mod(pos.y, 2.0)*varY, 2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = 100.;
    vec2 pos   = floor(fragCoord / size);

    fragColor  = pattern(pos, 1., 1.) * vec4(1.0, 1.0, 1.0, 1.0);
    //fragColor -= pattern(pos, 2., 1.) * vec4(1.0, 1.0, 1.0, 1.0);
}
