mat2 rotate(float a) {
	float s = sin(a), c = cos(a);
	return mat2(c, -s, s, c);
}

void mainImage(out vec4 fragColor, in vec2 C) {
    float zoom = 5. + 4.7 *sin(0.7 * iTime),
          skew = 0.6;
    vec2 R = iResolution.xy;
    C = ((C - 0.5 * R) * zoom / min(R.x, R.y)) * rotate(iTime);
    // Shift back with a slight skew.
    C += 1.5 + skew;

    vec4 color = vec4(1);
    for (float b = 0.; b < 0.7; b += 0.1) {
        vec2 v = abs(C - 1.5);
        float m = max(v.x, v.y);
        if (m < .5 || m > 1.5) {
            color = b * vec4(0.5, 1, 2, 0);
            break;
        }
        C = fract(C) * 3.;
    }
    fragColor = color;
}
