void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t=iTime;
    if(t<0.5)
    {
        fragColor=vec4(0.0,0.0,0.0,1.0);
        return;
    }
    if(length(fragCoord.xy-iMouse.xy)>(fragCoord.x/20.))
        discard;

    float red =((iMouse.x/500.)/sin(fragCoord.y*iTime/50.));
    float green =iMouse.y/500.;

	fragColor = vec4(red,green,.0,1.0);
}
