void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st  = fragCoord/iResolution.y;

    //this line creates the  mirror
 	st.x = 0.8 - abs(0.9 - st.x );

    float blue = sqrt(distance(st,vec2(0.2,0.4))*3.);

    float red = sqrt(distance(st,vec2(0.5,0.7))*3.);

    float yellow =sqrt(distance(st,vec2(0.5,0.2))*3.);

    vec3 colour= vec3(blue,red,yellow)-(iTime)/5.;


    //fract extracts the part of the image that falls within 0 to 1 (kinda). See https://thebookofshaders.com/glossary/?search=fract
    colour = fract(colour)/vec3(0.31);


    // Output to screen
    fragColor = 1.-vec4(colour,1.0);
}
