float fibonacci(in vec2 uv)
{
    float s = 0.0;

    for (int n = 0; n < 2000; n++)
    {
        float numb = float(n);

        float angle = numb * radians(137.51+iTime/50.);
        float r = 0.02 * sqrt(numb*1.);
        vec2 circlePos = r * vec2(cos(angle), sin(angle));

        s += (1.0 - r) * smoothstep(-1.5/iResolution.y, 0.0, 0.01 - length(circlePos - uv));
    }

    return s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord  - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3( fibonacci(uv) );

    fragColor = vec4(col, 1.0);

}
