// Tweak of sben's 'Flow of cells' (https://www.shadertoy.com/view/MlsGWX)
#define FIELD 35.0
#define HEIGHT 0.7
#define ITERATION 2.
#define TONE vec4(0.1,0.2,0.3,0)
#define SPEED 0.4

float eq(vec2 p,float t){
	float x = sin( p.y +cos(t+p.x*.2) ) * cos(p.x-t);
	x *= acos(x);
	return - x * abs(x-.5) * p.x/p.y;
}

void mainImage( out vec4 O, vec2 U ) {
	vec4 X=O;
	vec2  p = FIELD*(U / iResolution.xy  +.5);
	float t = iTime*SPEED,i,
         hs = 20.*(HEIGHT+cos(t)*.1),
	      x = eq(p,t), y = p.y-x;

	for(float i=0.; i<ITERATION; ++i)
		p.x *= 2.,
        X = x + vec4(0, eq(p,t+i+1.), eq(p,t+i+2.) ,0),
        x = X.z += X.y,
        O += TONE / abs(y-X-hs);
}
