#define PI 3.1415926f
#define PIx2 2.*PI
#define PI_HALF PI/2.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float sinDegree = sin(PI_HALF * iTime);
    float sinDegreeOffset = sinDegree * .1;  

    vec4 firstColor = texture(iChannel0, uv);
    vec4 secondColor = texture(iChannel1, uv);

    fragColor = firstColor;

    for (float offset = 0.0 ; offset < 2.2; offset += 0.2)
    {
        float tmp = -uv.x + offset; // y 方向上平移 offset 得到新的线条, 公式 y = -x + offset;

        // 在两条线之间（范围随时间变化）
        if (uv.y < (tmp + sinDegreeOffset) &&
                 uv.y > (tmp - sinDegreeOffset))      
        {
            fragColor = secondColor;
        }
    }

}
