#define G 32 // MORE => LESS

float trace(vec3 o, vec3 r){
    float d = 0.;
    for (int i = 0; i < G; i++){
        vec3 p = o + r * d;
        d += length(mod(p, 2.) - 1.) - .2 + sin(dot(floor(p/2.), vec3(9)))*.1;
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = iTime;
    vec2 uv = ((fragCoord/iResolution.y) * 2. - 1.);
    fragColor = vec4(vec3(cos(t) * .5 + .5,
                    .5 * sin(t) + .5,
                    sin(t / 3.1243)) * 
                    10. /
                        (1. + pow(
                            trace(vec3(t, cos(t), sin(t)),
                            normalize(vec3(uv, 1.7)) *
                            mat3(
                                cos(t / 5.),0.,-sin(t / 5.),
                                0.,1.,0.,
                                sin(t / 5.),0.,cos(t / 5.))), 2.) * .05
                                ), 1.);
}
