const int POINTS_COUNT = 32;

float random (float i) {
  return fract(sin(i)* 43758.5453123);
}

float noise(float x) {
  float i = floor(x);
  float f = fract(x);
  return mix(random(i), random(i + 1.0), smoothstep(0.,1.,f));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 st = fragCoord.xy / iResolution.xy;
  st.x *= iResolution.x / iResolution.y;
  st.x -= 0.35;
  vec3 color = vec3(.0);
  vec2 point[POINTS_COUNT];
  for (int i = 0; i < POINTS_COUNT; i++) {
    point[i] = vec2(noise(float(i) + sin(iTime)), noise(float(i) + 1. + iTime));
  }

  float m_dist = 1.;
  for (int i = 0; i < POINTS_COUNT; i++) {
    float dist = distance(st, point[i] + sin(iTime * (noise(float(i)) + 2.5)) / 4.);
    m_dist = min(m_dist, dist);
  }

  color += vec3(
		smoothstep(0.1, 0.3, m_dist),
		smoothstep(-0.1, -0.3, (1. - m_dist)),
		m_dist
  );

  fragColor = vec4(color, 1.0);
}
