#define S(a, b, t) smoothstep(a, b, t)

float N21(vec2 p)
{
    p = fract(p*vec2(651.34, 237.73));
    p += dot(p, p + 23.45);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p)
{
    float n = N21(p);
    return vec2(n, N21(p+n));
}

vec2 GetGridPoint(vec2 id, vec2 offs)
{
    vec2 n = N22(id + offs) * iTime;

    return offs + sin(n) * .3;
}

float DistToLine(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa, ba)/dot(ba,ba),0.,1.);
    return length(pa - ba*t);
}

float Line(vec2 p, vec2 a, vec2 b)
{
    float d = DistToLine(p, a, b);
    float m = S(.05, .005, d);
    m *= S(1.1,.6,length(a - b));
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy)/iResolution.y;
	uv *= 5.;

    vec2 index = floor(uv);
	vec2 gridUv = fract(uv) - .5;

    vec2 p[9];

    int i = 0;
    for(float y =-1. ;y <=1. ; y++)
    {
        for(float x =-1. ;x <=1. ; x++)
        {
            p[i++] = GetGridPoint(index, vec2(x,y));
        }
    }

    float result = 0.;
    for(int i=0; i< 9; i++)
    {
        result += Line(gridUv, p[4], p[i]);

        //vec2 j = (p[i] - gridUv) * 25.;
        //float sparkle = 1./dot(j,j);
        //result += sparkle;
    }
    result += Line(gridUv, p[1], p[3]);
    result += Line(gridUv, p[1], p[5]);
    result += Line(gridUv, p[3], p[7]);
    result += Line(gridUv, p[5], p[7]);    

    vec3 col = vec3(result);

    //if(gv.x >.48|| gv.y > .48) col = vec3(0,0,1);

    fragColor = vec4(col,1.0);
}
