#define Rotate(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float Circ(vec2 st, vec2 pCenter, float r)
{
    return smoothstep(0.0, 3./iResolution.y, length(st - pCenter) - (r * 0.5));
}

float Box(vec2 st, vec2 halfExtents)
{
    st = abs(st) - halfExtents;
    return smoothstep(0., 3./iResolution.y, max(st.x, st.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 st = (2. * fragCoord - iResolution.xy) / iResolution.y;

    st *= 6.0;

    float id = floor(st.x);
    st = fract(st);


    float c = 1.;
    float modTime = (iTime * 4.) + 6. + id;

    const float PI = 3.1415;
    const float halfPI = 3.1415 * 0.5;
    c *= Circ(st, 
              vec2(
                  min(sin(modTime), 0.),
                  sin((modTime + halfPI) * 0.5) * 0.5
              ), 
              0.2);

    st -= vec2(max(sin(modTime), 0.), 0.);

    float direction = sign(fract(modTime/(4. * PI)) - .5);//rotation direction flips

    float d = max(sin(modTime), 0.);
    st = (d > 0.) ? Rotate(modTime * direction) * st : st;
    c *= Box(st, vec2(0.05, 0.65));


    fragColor = vec4(vec3(1., 0., 0.) * c,1.0);
}
