void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // find distance to edges
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;
    float dMouse = sqrt(pow(mouse.x*2.0-uv.x*2.0, 2.0) + pow(mouse.y-uv.y, 2.0));
    float dist = (1.00-uv.y)*uv.y*(1.00-uv.x)*uv.x*75.0*dMouse;

    // set pixel color
    vec3 col = vec3(dMouse,dist, uv.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}
