void drawCircle(in vec2 point, in vec2 center, in float radius, in vec3 color, out vec3 col)
{
    if (insideCircle(point, center, radius))
    // if (simpleAA(point, center, radius))
    {
        col = color;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    // cute white circle A at the center of viewport
    vec2 centerA = iResolution.xy / 2.0;
    float radiusA = 88.0;
    drawCircle(fragCoord, centerA, radiusA, vec3(1), col);

    // small gray spot after the cursor's last click
    vec2 centerB = iMouse.xy;
    float radiusB = 8.0;
    drawCircle(fragCoord, centerB, radiusB, vec3(0.5), col);

    // blue spot when click the cursor
    vec2 centerC = iMouse.zw;
    float radiusC = 12.0;
    drawCircle(fragCoord, centerC, radiusC, vec3(0, 0, 1), col);

    // test origin point in the coordinate system
    drawCircle(fragCoord, vec2(0, 0), 10.0, vec3(1, 0, 0), col);

    fragColor = vec4(col, 1.0);
}
