const float f = 1.0 / 60.0 / 60.0 / 12.0; //12 hr period
//const float f = 1.0 / 60.0 / 60.0; //1 hr period

const float ep = .25; //max .4999
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = (fragCoord - iResolution.xy/2.0);
    float theta = atan(xy.y, xy.x);
    float r = length(xy);
    float r1 = floor(r);
    float frac = fract(r);
    float r2 = floor(r+1.0);

    float t = iDate.w * 2.0 * 3.141592653;
    //t = (iTime - 10.0) * 2.0 * 3.141592653;
    float theta1 = t * f * r1 + theta;
    float theta2 = t * f * r2 + theta;

    vec2 xy1 = r1 * vec2(cos(theta1), sin(theta1));
    vec2 xy2 = r2 * vec2(cos(theta2), sin(theta2));

    xy1 = (xy1 + iResolution.xy/2.0)/iResolution.xy;
    xy1 = (ep + xy1*(1.0-2.0*ep))*iChannelResolution[0].xy;
    xy2 = (xy2 + iResolution.xy/2.0)/iResolution.xy;
    xy2 = (ep + xy2*(1.0-2.0*ep))*iChannelResolution[0].xy;

    vec4 c1 = texelFetch(iChannel0, ivec2(xy1), 0);
    vec4 c2 = texelFetch(iChannel0, ivec2(xy2), 0);



    // Output to screen
    fragColor = mix(c1, c2, frac);
}
