renderpass.code:
float triangle(in vec2 p) {
   return step(p.y, min(p.x,1.-p.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv1 = (fragCoord - iResolution.xy /2.)/iResolution.x;

    float a = (((atan(uv1.y, uv1.x) / 3.14159) + 1.) / 2.);
    float r = length(uv1);

    vec2 uv = vec2(a, r*(sin(iTime)+2.));

    float size = 30.;
    float i = floor(uv.y*size)/(size);
    fragColor = 1.-vec4(triangle(fract((uv-i)*size/2.0))); 

    fragColor *= step(.1, r);
}



