float norm_sin(float val) {
	return (sin(val)+1.0)/2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	float b = 300.0;
	float h = 100.0;
	float w = 30.0;

	bool xflip = mod(fragCoord.x, w) < w/2.0 ? true : false;

	float ygrade = ((fragCoord.y - (floor(fragCoord.y/h))*h)/h);

	float grade = xflip ? ygrade : 1.0-ygrade;

	fragColor = vec4( norm_sin(iTime/2.0)/grade, norm_sin(iTime/5.)*grade, norm_sin(iTime/10.)*grade,1.0 );

}
