vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}
//  1 out, 1 in...
float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}


float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float rand(float t)
{
    return hash11(t);
}


float cubicPulse( float c, float w, float x ){
    x = abs(x - c);
    if( x>w ) return 0.0;
    x /= w;
    return 1.0 - x*x*(3.0-2.0*x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv + 0.5) * vec2(iResolution.x/iResolution.y, 1.0);

    float t = iTime/5.0;

    float Z = 3.0+5.0*rand(floor(t));

    vec2 uvg = Z*uv;

    uvg = fract(uvg);

    vec2 offset = (uvg-0.5);
    //float angle = cubicPulse(0.5, 0.2, fract(iTime));
    float angle = 2.0*3.1415926*smoothstep(0.00, 1.0, fract(t));

    offset = rotate(offset, angle);
    offset += 0.5;

    vec2 uv2 = (floor(Z*uv)+offset)/Z;


    // Output to screen
    fragColor =  texture(iChannel0, uv2);
}
