float circle(in vec2 pos, in float t, in float mult)
{
    vec2 center = vec2(0.5,0.5 * iResolution.y / iResolution.y);
    vec2 v = vec2(center.x + cos(t) + 0.10, center.y + sin(t) * 0.1) - pos;

    float dist2 = dot(v, v);
    float circles = 0.2 + cos(dist2 * mult) * 0.2;
    float darkening = smoothstep(0.0, 0.5, 1.0 - length(v) * 2.0);

    return circles + darkening;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pos = fragCoord.xy / iResolution.xy;
    float t = iTime * 1.25;

    //float r = circle(pos, t + 20.8,  10.0 + sin(t * 0.6 + 1.0) * 1.0);
    //float g = circle(pos, t + 0.5,  10.0 + sin(t * 0.6 + 2.0) * 1.0);
    float b = circle(pos, t + 20.3,  10.0 + sin(t * 0.6 + 3.0) * 1.0);
    //float w = circle(pos, t + 0.1, 10.0 + sin(t + 4.0) * 2.0) * 1.35;
    float y = circle(pos, t + 2.1, 10.0 + sin(t + 4.0) * 2.0) * 1.35;
    fragColor = vec4(y*b, y/b, b*y,  1.0);
}
