#define S(a,b,t) smoothstep(a,b,t)

float DistLine(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    return length(pa-ba*t);
}

float N21(vec2 p)
{
    p = fract(p*vec2(233.34, 851.73));
    p += dot(p,p+23.45);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p)
{
    float n = N21(p);
    return vec2(n , N21(p+n));
}

vec2 GetPos(vec2 id, vec2 offs)
{
    vec2 n = N22(id+offs)*iTime;
    return offs+sin(n)*0.3;
}

float Line(vec2 p , vec2 a, vec2 b)
{
    float d = DistLine(p,a,b);
    float m = S(.03,.01,d);
    m *= S(1.2,.8,length(a-b));
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Origin at the middle and aspect ratio correct
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;

    float m = 0.;

    uv *= 10.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    vec2 p[9];
    int i = 0;
    for(float y=-1. ; y<=1. ; y++)
    {
        for(float x =-1. ; x<=1. ; x++)
        {
            p[i++] = GetPos(id,vec2(x,y));         
        }
    }   

    for(int i = 0 ; i<9 ; i++)
    {
        m += Line(gv, p[4] , p[i]);

        vec2 j = (p[i]-gv)*15.;
        float spk = 1./ dot(j,j);
        m += spk*(sin(iTime+p[i].x*5.)*.5+.5);
    }

    m += Line(gv, p[1] , p[3]);
    m += Line(gv, p[1] , p[5]);
    m += Line(gv, p[7] , p[3]);
    m += Line(gv, p[7] , p[5]);

    vec3 col = vec3(m);

    //if(gv.x > .48 || gv.y > .48) col = vec3(1,0,0);

    fragColor = vec4(col,1.0);
}
