// Rubino Marc - 2021
// https://www.youtube.com/channel/UC4QabYdHqsMLv1WiwtYRzRw
// DEUTSCHLAND

float pas = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 r = vec3(1.0,0.0,0.0);
    vec3 g = vec3(0.0,1.0,0.0);

    vec3 col = vec3(0.);

    pas += 0.33;
    float cr = step(pas,1.-uv.y);
    col = vec3(cr*r);
    fragColor = vec4(col,1.0);

    pas += 0.33;
    float cg = step(pas,1.-uv.y);
    col = vec3(cg*g);
    fragColor += vec4(col,1.0);
}
