void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float s=texture(iChannel1,vec2(uv.x,.75)).r;
    float t1=texture(iChannel1,vec2(uv.x-.5/iResolution.x,.75)).r;
    float t2=texture(iChannel1,vec2(uv.x+.5/iResolution.x,.75)).r;
    float m = (t2-t1)*iResolution.x;
    float w = 5.;
    float h = sqrt((w*w + m*m));

    vec4 a=texture(iChannel2,vec2(uv.x,uv.y));
    vec4 v = texture(iChannel0, uv);
	fragColor.r=clamp(h-abs((t1+t2)/2.-uv.y)*iResolution.y,0.,h)/h;
    fragColor.b = 0.0;
    fragColor.g=a.r;
    fragColor.rbg += v.rgb/10.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fc1 = fragCoord - vec2(1.5,0.);
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uv1 = fc1 / iResolution.xy;
    fragColor = texture(iChannel0,uv1);
    if (fragCoord.x < 1.) {
        vec4 s = texture(iChannel2, vec2(uv.y*.25,0.25));
        //vec4 m = texture(iChannel2, vec2(uv.y,.75));
        //if (uv.y > m.r) s.g = 1.0;
        fragColor = s;
    }
}
