void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = cos(iTime/2.)*sin(fragCoord.x/30.)+iTime*0.001225;
    float y = sin(iTime/20.)*cos(fragCoord.y/.0013)+iTime*.0625; 

    fragCoord = vec2(x,y);
    float z = fragCoord.x/fragCoord.y+iTime*.125;
	fragColor = vec4(smoothstep(tan(z+x),0.6,0.8), 
                     smoothstep(sin(z+x-y),.8,.6), 
                     smoothstep(cos(z+y),.3,.5) ,.5);
}
