//fork of https://www.shadertoy.com/view/tll3R8 by KaloyanKaludov

vec3   srgb(vec3 c){ return pow(c,vec3(   2.2)); }
vec3 unsrgb(vec3 c){ return pow(c,vec3(1./2.2)); }
float sat(float x){ return clamp (x, 0.,1.);}
vec2 sat(vec2 x){ return clamp(x, vec2(0.),vec2(1.));}
vec3 sat(vec3 x){ return clamp(x, vec3(0.),vec3(1.));}
vec4 sat(vec4 x){ return clamp(x, vec4(0.),vec4(1.));}

vec3 iridescence(float h){
    #define RATHER_HOT unsrgb(vec3(1.,.0,.0))
    #define QUITE_HOT  unsrgb(vec3(1.,.4,.0))
    #define VERY_HOT   unsrgb(vec3(1.,.98,.9))
    //color ramp
    vec3 w= vec3(h*2.);
    w= 1.-abs(w-vec3(0.,1.,2.));
    w= sat(w);
    return RATHER_HOT*w.x + QUITE_HOT*w.y + VERY_HOT*w.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float l;
    l+= texture(iChannel0, uv     + vec2(iTime, iTime)/64.0).g;
    l+= texture(iChannel0, uv*2.5 + vec2(iTime,-iTime)/20.0).g;
    l/= 2.;
    l= smoothstep(0., .75, l);
    if(l<.5)
		l/= 2.;
    if(l>1.)
        l*= 1.5;
    l/= 1.5/1.5;

    fragColor.rgb = srgb(iridescence(l));
}
